import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
//import SideBar from "./components/sidebar/SideBar";
//import Content from "./components/content/Content";
import Messagerie from "./components/Logistics/Messagerie";
import Affretement from "./components/Logistics/Affretement";
import Bert from "./components/Logistics/Bert";
import Uploader from "./components/Uploader/Uploader";
import Index from './views/Index/Index';
import Home from './components/Home/Home';
import LandingPage from './components/LandingPage/LandingPage';
import Admin from './components/Admin';
//import Login from './components/LandingPage/Authentification/Login';
//import Signup from './components/LandingPage/Authentification/Signup';

import {BrowserRouter as Router, Switch , Route} from 'react-router-dom';


class App extends React.Component {
  constructor(props) {
    super(props);

    // Moblie first
    this.state = {
      isOpen: false,
      isMobile: true
    };

    this.previousWidth = -1;
  }

  updateWidth() {
    const width = window.innerWidth;
    const widthLimit = 576;
    const isMobile = width <= widthLimit;
    const wasMobile = this.previousWidth <= widthLimit;

    if (isMobile !== wasMobile) {
      this.setState({
        isOpen: !isMobile
      });
    }

    this.previousWidth = width;
  }

  /**
   * Add event listener
   */
  componentDidMount() {
    this.updateWidth();
    window.addEventListener("resize", this.updateWidth.bind(this));
  }

  /**
   * Remove event listener
   */
  componentWillUnmount() {
    window.removeEventListener("resize", this.updateWidth.bind(this));
  }

  toggle = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  render() {
    return (
      <Router>
      <div className="App wrapper">
        
       {/* <SideBar path="/admin" toggle={this.toggle} isOpen={this.state.isOpen}/>
        <Content path="/admin" toggle={this.toggle} isOpen={this.state.isOpen}/>
        <Route path="/sign-in" component={Login} />
        <Route path="/sign-up" component={Signup} />*/}
        <Route path="/admin" component={Admin}/>
        <Route path="/Home" component={Home}/>
        <Route path="/Messagerie" component={Messagerie}/>
        <Route path="/Affretement" component={Affretement}/>
        <Route path="/Bert" component={Bert}/>
        <Route path="/Uploader" component={Uploader}/>
        <Route path="/LandingPage" component={LandingPage}/>

       
      </div>
      </Router>
    );
  }
}

export default App;
